mapper.update:
		UPDATE TBL_BOARD
    	SET 
    	TITLE = #{title}, CONTENTS = #{contents} 
    	WHERE BOARDNO = #{boardNo}

	UPDATE TBL_COMMENT
	SET
    CONTENTS = #{contents}
	WHERE COMMENTNO = #{commentNo}

mapper.select:
SELECT COMMENTNO, USER_ID, CONTENTS, BOARDNO
FROM TBL_COMMENT
WHERE COMMENTNO = #{commentNo}
